import React, {Component} from 'react'
import './style.css'

let baseUrl = 'http://localhost:3003'

class UserPage extends Component {
    constructor(props){
        super(props)
        this.state = {
            apiUrl:'https://api.seatgeek.com/2/events?',
            apikey: "&client_id=" + process.env.REACT_APP_SEATGEEK_API_KEY,
            sessionSecret: "&client_secret=" + process.env.REACT_APP_SECRET_SESSION,
            query: 'performers.id=',
            performers: [],
            searchUrl: '',
            performers: [
                {
                    id: 8987,
                    name: 'Grouplove',
                    type: 'Band'
                },
                {
                    id: 32,
                    name: 'Coldplay',
                    type: 'Band'
                },
            ]
        }
    }

    getUser = () =>{
        fetch(baseUrl + '/users',{
            credentials:"include"
        })
        .then(res =>{
            if(res.status === 200){
                return res.json()
            }else{
                return []
            }
        }).then(data => {
            console.log(data)
        })
    }

    getEvents = () =>{
        let performerIds = this.state.performers.map(id => id.id)
        console.log(this.state.apiUrl + "performers.id=" + performerIds.join(',') + this.state.apikey )
        fetch(this.state.apiUrl + "performers.id=" + performerIds.join(',') + this.state.apikey)
        .then(res => {
            return res.json()
        }).then(data =>{
            console.log(data)
            this.setState({
                events: data
            })
        })
    }

    addPerfromer = (newPerformer) =>{
        const copyPerformers = [...this.state.performers]
        copyPerformers.push(newPerformer)
        this.setState({
            performers: copyPerformers
        })
    }
    componentDidMount = () =>{
        this.getEvents()
    }




    render(){
        return(
            <>
                <h1>Hello!</h1>
                <NewPerformer baseUrl={baseUrl} addPerformer={this.addPerformer}/>

            </>
        )
    }
}

export default UserPage
