import React, { Component } from 'react'
import './style.css'

export default class NewForm extends Component {
  constructor(props) {
    super(props)

    this.state = {
        performerApiUrl: 'https://api.seatgeek.com/2/performers?q=',
        id: '',
        name: '', 
        type: '', 
        searchResult: []
    }
  }

  handleChange = (event) => {
    // console.log(event.target.value)
    this.setState({
      name: event.target.value
    })
  }

  handleSubmit = (event) => {
    event.preventDefault()
    console.log(this.state.name)
    this.findPerformer(this.state.name)

  }

//searches SeatGeek API for performer similar to user search
  findPerformer = (name) =>{
      fetch(this.state.performerApiUrl + name + this.props.apikey)
        .then(res =>{
            return res.json()
        }).then(data => {
            console.log("api fetch performer result: " , data.performers)
            let performerResult = data.performers.filter(performer => {
                if(performer.name.toLowerCase().includes(name.toLowerCase())){
                    return performer
                }
            })
            console.log("api fetch similar performer result: " , performerResult)
            this.setState({
                searchResult: performerResult
            }) 
        })
  }

  addPerformerToUser = (performer) =>{
    fetch(this.props.baseUrl + '/users/addArtist', {
        method: 'PUT',
        body: JSON.stringify({id: this.state.id, name: this.state.name, type: this.state.type}),
        headers: {
          'Content-Type': 'application/json'
        },
    }).then( res => {
        return res.json()
    }).then( data => {
      // console.log(data)
      this.props.addPerformer(data)
      this.setState({
        id: '',
        name: '', 
        type: ''
      })
    }).catch (error => console.error({'Error': error}))
  }

  //for testing
//   componentDidMount=()=>{
//       this.findPerformer('group')
//   }


  render() {
      console.log("results: ", this.state.searchResult)
    return (
        <>
        <div className="user-form">
      <form onSubmit={this.handleSubmit}>
        <label htmlFor="name">Name: </label>
        <input type="text" id="name" name="name" onChange={ (e) => this.handleChange(e)} value={this.state.name} />
        <input className="btn-primary" type="submit" placeholder="Performer's name" value="Search for performer" />
      </form>

        <table>
            <tbody>
                {this.state.searchResult.map((performer, i) =>{
                    return(
                        <tr key={performer.id}>
                            <td><button className="btn-performer" onSubmit={() =>this.addPerformerToUser(performer)}>Add Artist</button></td>
                            <td>{performer.name}</td>
                            <td>{performer.type}</td>
                        </tr>
                    )
                })}
            </tbody>
        </table>

        </div>
      </>
    )
  }

}
