import React, { Component } from 'react'

export default class NewForm extends Component {
  constructor(props) {
    super(props)

    this.state = {
        performerApiUrl: 'https://api.seatgeek.com/2/performers?slug=',
        id: '',
        name: '', 
        type: ''
    }
  }

  handleChange = (event) => {
    // console.log(event.target.value)
    this.setState({
      name: event.target.value
    })
  }

  handleSubmit = (event) => {
    event.preventDefault()
    // fetch
    this.findPerformer(this.state.name)
    fetch(this.props.baseUrl + '/users/addArtist', {
        method: 'POST',
        body: JSON.stringify({id: this.state.id, name: this.state.name, type: this.state.type}),
        headers: {
          'Content-Type': 'application/json'
        },
    }).then( res => {
        return res.json()
    }).then( data => {
      // console.log(data)
      this.props.addPerformer(data)
      this.setState({
        id: '',
        name: '', 
        type: ''
      })
    }).catch (error => console.error({'Error': error}))
  }

  findPerformer = (name) =>{
      fetch(this.state.performerApiUrl + name + this.props.apikey)
        .then(res =>{
            return res.json()
        }).then(data => {
            console.log(data)
            return data
        })
  }

//   componentDidMount=()=>{
//       this.findPerformer('coldplay')
//   }


  render() {
    return (
      <form onSubmit={this.handleSubmit}>
        <label htmlFor="name">Name: </label>
        <input type="text" id="name" name="name" onChange={ (e) => this.handleChange(e)} value={this.state.name} />
        <input type="submit" value="Add a performer" />
      </form>
    )
  }

}
